import React, { useState } from "react";
import Button from "./components/Button";

import './App.css';

function App() {

  const [showPara, setShowPara] = useState(false)

  return (
    <div className="app">
      <h1>Hi there!</h1>
      {showPara && <p>This is new!</p>}
      <Button onClick={() => setShowPara(true)}>Show</Button>
    </div>
  );
}

export default App;
